boolean win = false;
boolean p1Turn = true;

char[] cells = new char[9];
for (int i = 0; i < cells.length; i++)
    cells[i] = '_';
do {
    char symbol = p1Turn ? 'x' : 'o';
    String player = p1Turn ? "Player 1"  : "Player 2" ;
    int row = 0 , col = 0;
    do {
        System.out.print(player + ", your turn! " );
        row = scanner.nextInt();
        col = scanner.nextInt();
        if (row >= 0 && row <= 2 && col >= 0 && 
            col <= 2 && cells[row * 3 + col] == '_')
            break;
        System.out.println( "Invalid" );
    } while (true);
    cells[row * 3 + col] = symbol;
    //print cells
    for (int j = 0; j < 3; j++) {
        for (int i = 0; i < 3; i++)
            System.out.print(cells[j * 3 + i] + " ");
        System.out.println();
    }   //check horizontal
    for (int r = 0; r < 3; r++)
        if (cells[r * 3] == cells[r * 3 + 1] &&
            cells[r * 3] == cells[r * 3 + 2] &&
            cells[r * 3] == symbol)
            win = true;
    //check vertical
    for (int c = 0; c < 3; c++)
        if (cells[c] == cells[c + 3] &&
            cells[c] == cells[c + 6] &&
            cells[c] == symbol)
            win = true;
    //check diagonal
    if (cells[ 0] == cells[ 4] &&
        cells[ 0] == cells[ 8] &&
        cells[ 0] == symbol)
        win = true;
    if (cells[ 2] == cells[ 4] &&
        cells[ 2] == cells[ 6] &&
        cells[ 2] == symbol)
        win = true;
    if (win)
        System.out.println(player + " you win!" );
    p1Turn = !p1Turn;
} while (!win);

